import styled from 'styled-components';
import React from 'react';
import Navbar from '../component/navbar';
import CardPoke from '../component/card';



import axios from 'axios';

const Wrapper = styled.div
`   display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const Styleddiv = styled.div`

  display: flex;
  align-items: Left;
  flex-flow: column;
  height: 10%;
  width: 80%;
  padding: 20px;
  padding-bottom : 40px;
  margin-bottom: 8% auto;
  margin-top: 13% auto;
  border: 1px solid #fffff;
  border-radius: 9px;
  background: #f7f7f7;
  h2 {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
  }
  button {
    background: #ff8019;
    color: #fff;
    padding: 11px;
    margin-top: 15px;
    width: 80%;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
  }
`;

const Bariscard = styled.div
`
  content: "";
  display: table;
  align-items: center;
  clear: both;  
  margin:15px 20px;
`;



class Forestpage extends React.Component{
constructor(props){
    super(props);
    this.state={
        pokemonData:[],
    }
}
    

    

    componentDidMount()
    {
        document.body.style.backgroundColor = "#dbdbdb";

        async function fetchData() {
            
            const rand = Math.random() * (70 - 7) + 1;
            const random = Math.round(rand);
            const initialURL = 'https://pokeapi.co/api/v2/pokemon?offset='+random+'&limit=30';
            console.log(initialURL);
            
            axios.get(initialURL).then((respons)=>{
            this.setState({
                pokemonData : respons.data.results
            })
                 
            });
           
            
          }
          fetchData();
          
   
          
    } 





    render(){
        let pokeCard = this.state.pokemonData.map(function(pokemon,i){
            return <CardPoke key={i} namapokemon={pokemon.nama} url={pokemon.url}/>;
        });
        return(
            <Wrapper>
               <Navbar/>
                <Styleddiv>
                    <h2>POKEMON FOREST </h2>
                    <Bariscard> 
                        {pokeCard}                        
                    </Bariscard>
                </Styleddiv>
            </Wrapper>
        );
    }
}
export default Forestpage;
