import React from 'react';
import styled from 'styled-components';



const Wrapper = styled.div
`   display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`

const StyledLogin = styled.div`


  display: flex;
  align-items: center;
  flex-flow: column;
  width: 250px;
  height: 300px;
  margin: 200px auto;
  border: 1px solid #fffff;
  border-radius: 9px;
  background: #f7f7f7;
  h2 {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
  }
  button {
    background: blue;
    color: #fff;
    padding: 10px;
    margin: 5px;
    width: 150px;
    border: none;
    border-radius: 8px;
    box-sizing: border-box;
  }
`;

const StyledInput = styled.input`
  border: 1px solid #000;
  border-radius: 5px;
  padding: 10px;
  margin: 5px;
  width: 150px;
  
`;


class Loginpage extends React.Component{

    componentDidMount()
    {
        document.body.style.backgroundColor = "linear-gradient(45deg, #49D49D 10%, #A2C7E5 90%);";
    }
    render(){
        return(
            <Wrapper>
            <StyledLogin>

            
            <img src/>
            <StyledInput type="text" placeholder="email" />
            <StyledInput type="password" placeholder="password" />
            <button>Login</button>
          </StyledLogin>
           </Wrapper>
        );
    }
}

export default Loginpage;
