import styled from 'styled-components';
import React from 'react';



const Kolomcard = styled.div
`
width: 100%;
display: block;
margin-bottom: 20px;
margin-left:4px;
float: left;
width: 15%;
padding: 0 4px;    
`;

const Card = styled.div
`
box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  padding: 12px;
  text-align: center;
  background-color: #fffff;
`;

const Gambar = styled.img
`
width : 100%;
height :100%;

`;

const Tombol = styled.button
`
background: #ff8019;
    color: #fff;
    padding: 11px;
    cursor: pointer;
    width: 40%;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
`;

const Namacard =  styled.div
`
font-family: "Lucida Console", Monaco, monospace;
margin:1px;
font-size:12px;
`;




class CardPoke extends React.Component{
    constructor(props){
        super(props);
        this.state={
            namaPokemon: this.props.namaPokemon,
            gambarurl:this.props.url,
           
        }

    }

    componentDidMount()
    {
          
          
    } 
    



    render(){
        return(
            <Kolomcard>
            <Card>
               <Gambar src={this.state.gambarurl}/>
                <Namacard>{this.state.namaPokemon}</Namacard>
                <a><Tombol>SAVE</Tombol></a>
            </Card>
        </Kolomcard>
        );
    }
}

export default CardPoke;
