import styled from 'styled-components';
import Link from 'next/link';


const Wrapper = styled.div
`   display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;
const Logo = styled.img
`
border-radius: 10%;
max-width: 80%;
max-height: 80%;
`;
const StyledLogin = styled.div`

  display: flex;
  align-items: center;
  flex-flow: column;
  width: 30%;
  padding: 20px;
  padding-bottom : 90px;
  margin: 200px auto;
  border: 1px solid #fffff;
  border-radius: 9px;
  background: #f7f7f7;
  h2 {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
  }
  button {
    background: #ff8019;
    color: #fff;
    padding: 11px;
    margin-top: 15px;
    width: 80%;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
  }
`;


const StyledInput = styled.input`
display: block;
width: 70%;
padding: 15px;
margin-top: 15px;
font-family: $font-family;
-webkit-appearance: none;
border: 0;
outline: 0;
transition: 0.3s;

&:focus {
    background: darken(#fff, 3%);
}
  
`;

const Registerlink = styled.a
`
margin-top: 12px;
font-family: monospace;
color: #4d7d5e;
font-size:90%;
width: 80%;
cursor: pointer;

align-text: center;
`


class Loginpage extends React.Component{

    componentDidMount()
    {
        document.body.style.backgroundColor = "#dbdbdb";
    }
    render(){
        return(
            <Wrapper>
                <StyledLogin>
                    <Logo src="/pokekoo.png" alt="pokekoo"/>
                    <StyledInput type="text" placeholder="email" />
                    <StyledInput type="password" placeholder="password" />
                    <button>LOGIN</button>
                    <Link href="/registerpage"><Registerlink>Belum punya akun? <b> Buat Akun Sekarang</b></Registerlink></Link>
                </StyledLogin>
           </Wrapper>
        );
    }
}

export default Loginpage;
