import styled from 'styled-components';
import React from 'react';
import Navbar from '../component/navbar';
import CardPoke from '../component/card';
import axios from 'axios';

const Wrapper = styled.div
`   display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const Styleddiv = styled.div`

  display: flex;
  align-items: Left;
  flex-flow: column;
  height: 10%;
  width: 80%;
  padding: 20px;
  padding-bottom : 40px;
  margin-bottom: 8% auto;
  margin-top: 13% auto;
  border: 1px solid #fffff;
  border-radius: 9px;
  background: #f7f7f7;
  h2 {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
  }
  button {
    background: #ff8019;
    color: #fff;
    padding: 11px;
    margin-top: 15px;
    width: 80%;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
  }
`;

const Bariscard = styled.div
`
  content: "";
  display: table;
  align-items: center;
  clear: both;  
  margin:15px 20px;
`;

Forestpage.getInitialProps = async (ctx) => {
    const rand = Math.round(Math.random() * (700 - 1) + 1);
    const initialURL = 'https://pokeapi.co/api/v2/pokemon?offset='+rand+'&limit=30';
    
    const res = await axios.get(initialURL);
    
    
    return {
        allPokemonData : res.results
    };
  }

  

export default function Forestpage()
{
    
const pokemon = allPokemonData.map(allPokemonData => <div>{allPokemonData.name}</div>)
    return(
        <Wrapper>
               <Navbar/>
                <Styleddiv>
                    <h2>POKEMON FOREST </h2>
                    <Bariscard> 
                    {pokemon}
                    </Bariscard>
                </Styleddiv>
            </Wrapper>
    );
}
