import React from 'react'
import  'isomorphic-unfetch'
import {getId} from '../service/pokemon'


const Testpage = ({pokemon = [], count}) =>(
    <div>
      ss
        <ul className="list">
          {pokemon.map(pokemon => {
           
            return (
              <li key={pokemon.name} >
                <span>{pokemon.url} </span>
                
              </li>
            )
          })}
        </ul>
    </div>
)

Testpage.getInitialProps = async ({ req }) => {
    const rand = Math.round(Math.random() * (964 - 1) + 1);
    const initialURL = 'https://pokeapi.co/api/v2/pokemon?offset='+rand+'&limit=30';
    
    const fetchRes = await fetch(initialURL)
    const res = await fetchRes.json()

  

    const data = res.results
    const count = res.count 

    const PokeData = await Promise.all(data.map(ppoke =>{
      const ppokeRec =  fetch(ppoke.url)
      return ppokeRec
    }))


   
    return {
      pokemon: PokeData,
      count,
    }
  }
  export default Testpage
