import styled from 'styled-components';
import React from 'react';
import Navbar from '../component/navbar';
import CardPoke from '../component/card';
import fetch from 'isomorphic-unfetch';

const Wrapper = styled.div
`   display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const Styleddiv = styled.div`

  display: flex;
  align-items: Left;
  flex-flow: column;
  height: 10%;
  width: 80%;ss
  padding: 20px;
  padding-bottom : 40px;
  margin-bottom: 8% auto;
  margin-top: 13% auto;
  border: 1px solid #fffff;
  border-radius: 9px;
  background: #f7f7f7;
  h2 {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
  }
  button {
    background: #ff8019;
    color: #fff;
    padding: 11px;
    margin-top: 15px;
    width: 80%;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
  }
`;

const Bariscard = styled.div
`
  content: "";
  display: table;
  align-items: center;
  clear: both;  
  margin:15px 20px;
`;

const Forestpage = ({pokemon = [], count}) =>(

    <Wrapper>
      <Navbar/>
        <Styleddiv>
            <h2>POKEMON FOREST </h2>
             <Bariscard> 
                {pokemon.map(pokemon =>{ return(
                    <div key={pokemon.name}>{pokemon.name} {pokemon.url}</div>
                )})}
             </Bariscard>
         </Styleddiv>
     </Wrapper>
   
)

Forestpage.getInitialProps = async ({ req }) => {
  const rand = Math.round(Math.random() * (964 - 1) + 1);
  const initialURL = 'https://pokeapi.co/api/v2/pokemon?offset='+rand+'&limit=30';
  
  const fetchRes = await fetch(initialURL)
  const res = await fetchRes.json()

  const data = res.results
  const count = res.count 
 
  return {
    pokemon: data,
    count,
  }
}
export default Forestpage



